var mongoose = require('mongoose');
var Schema = mongoose.Schema;

var UserMed = mongoose.Schema({
  name: String,
  start_date: Date,
  end_date:Date,
  course: Number,
  desc: String,
  dosage: String
})

var userSchema = mongoose.Schema({
  token : String,
  email: String,
  phno: String,
  hashed_password: String,
  salt : String,
  temp_str:String,
  meds: [ UserMed ]
});

mongoose.connect('mongodb://localhost:27017/node-android2');
module.exports = mongoose.model('users', userSchema);
